CustomField.formula:
IF(NumberOfErrors__c  = 0 && NumberOfWarnings__c = 0, IMAGE('/img/msg_icons/confirm16.png', 'Successful'),
	IF(NumberOfErrors__c  > 0 && NumberOfErrors__c =  TotalJobItems__c , IMAGE('/img/msg_icons/error16.png', 'Failed'),
     IF((NumberOfErrors__c  > 0 && NumberOfErrors__c <  TotalJobItems__c) || NumberOfWarnings__c > 0 , IMAGE('/img/msg_icons/warning16.png', 'Warning'), null)
	)
)
